import cv2
import numpy as np
img = cv2.imread('img/dcz_noise.png')
h, w, c = img.shape
print(h, w, c)
for i in range(1, h-1):
for j in range(1, w-1):
for y in range(c):
if abs(img[i, j, y] - img[i-1, j, y]) > 30 and abs(img[i, j, y] - img[i+1, j, y]):
img[i, j, y] = img[i, j, y]
else:
img[i, j, y] = 0
cv2.imshow('sucha', img)
cv2.waitKey(0)
cv2.destroyAllWindows()

