import cv2
import numpy as np
def compute_histogram(image, bins=32):
hist = cv2.calcHist([image], [0, 1, 2], None, [bins, bins, bins], [0, 256, 0, 256, 0, 256])
hist = cv2.normalize(hist, hist).flatten()
return hist
def compare_histograms(hist1, hist2, method):
if method == 'correlation':
return cv2.compareHist(hist1, hist2, cv2.HISTCMP_CORREL)
elif method == 'chi-square':
return cv2.compareHist(hist1, hist2, cv2.HISTCMP_CHISQR)
elif method == 'bhattacharyya':
return cv2.compareHist(hist1, hist2, cv2.HISTCMP_BHATTACHARYYA)
else:
raise ValueError("Method not supported")
image_paths = ['img/tiger1.jpg', 'img/tiger2.jpg', 'img/tiger3.jpg']
images = [cv2.imread(path) for path in image_paths]
hists = [compute_histogram(image) for image in images]
methods = ['correlation', 'chi-square', 'bhattacharyya']
for method in methods:
print(f"Similarity using {method}:")
for i in range(len(images)):
for j in range(i + 1, len(images)):
similarity = compare_histograms(hists[i], hists[j], method)
print(f"tiger{i+1} vs tiger{j+1}: {similarity}")
print()

