import cv2
import numpy as np
from skimage.metrics import structural_similarity as ssim
def read_image(file_path):
    return cv2.cvtColor(cv2.imread(file_path), cv2.COLOR_BGR2GRAY)
def calculate_histogram(image, bins=256):
    histogram = cv2.calcHist([image], [0], None, [bins], [0, 256])
    return cv2.normalize(histogram, histogram).flatten()
def correlation(image1, image2):
    return cv2.compareHist(image1, image2, cv2.HISTCMP_CORREL)
def euclidean_distance(image1, image2):
    return np.sqrt(np.sum((image1 - image2) ** 2))
def ssim_index(image1, image2):
    return ssim(image1, image2, multichannel=True)
image_paths = ['img/tiger1.jpg', 'img/tiger2.jpg', 'img/tiger3.jpg']
images = [read_image(path) for path in image_paths]
for i, img in enumerate(images):
    hist = calculate_histogram(img, 32)  # 每个通道32个值
    print(f"Histogram for tiger{i+1}: {hist}")
similarities = np.zeros((3, 3))
for i in range(3):
    for j in range(3):
        if i != j:
            hist1 = calculate_histogram(images[i], 32)
            hist2 = calculate_histogram(images[j], 32)
            similarities[i, j] = correlation(hist1, hist2)
print("Similarity matrix (correlation):")
print(similarities)
assert np.max(similarities) == similarities[0, 1], "tiger1 and tiger2 should have the highest similarity"
for i in range(3):
    for j in range(3):
        if i != j:
            similarities[i, j] = euclidean_distance(calculate_histogram(images[i], 32), calculate_histogram(images[j], 32))
            similarities[i, j] = ssim_index(images[i], images[j])
print("Similarity matrix (Euclidean distance):")
print(similarities)
print("Similarity matrix (SSIM):")
print(similarities)

