import cv2
import numpy as np

def nothing(x):
    pass


windowName = "yangzhengrong"
cv2.namedWindow(windowName)

cv2.createTrackbar('Type', windowName, 0, 3, nothing)
cv2.createTrackbar('Value', windowName, 127, 255, nothing)

img = cv2.imread('img/jp.jpg', cv2.IMREAD_GRAYSCALE)

if img is None:
    print("Error: Image not found.")
    exit()


while True:

    Type = cv2.getTrackbarPos('Type', windowName)
    Value = cv2.getTrackbarPos('Value', windowName)


    if Type == 0:
        ret, dst = cv2.threshold(img, Value, 255, cv2.THRESH_BINARY)
    elif Type == 1:
        ret, dst = cv2.threshold(img, Value, 255, cv2.THRESH_BINARY_INV)
    elif Type == 2:
        ret, dst = cv2.threshold(img, Value, 255, cv2.THRESH_TRUNC)
    elif Type == 3:
        ret, dst = cv2.threshold(img, Value, 255, cv2.THRESH_TOZERO)
    else:
        ret, dst = cv2.threshold(img, Value, 255, cv2.THRESH_TOZERO_INV)


    cv2.imshow(windowName, dst)


    if cv2.waitKey(1) & 0xFF == 27:
        break


cv2.destroyAllWindows()
